export function getRandomMechanics(count: number, exclude: number[] = []): Mechanic[] {
  const available = MECHANICS.filter((m) => !exclude.includes(m.id));
  const shuffled = [...available].sort(() => Math.random() - 0.5);
  return shuffled.slice(0, count);
}
